import React, { useState } from "react";
import Card from "../UI/Card";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";

import './Expenses.css';

function Expenses(props) {

    const [dropDSelection, setSelection] = useState('2020');
    const [filteredExpenses, setFilteredExpenses] = useState(props.expense)


    const dropDownChangeHandler = (selection) => {

        setSelection(prevSelection => selection);

        setFilteredExpenses((prevFiltered) => {

            return prevFiltered.filter((expense) => {
                return expense.date.toLocaleString('en-US', {year: 'numeric'}).toString() === dropDSelection;
            })
        });

    }


    return (

        <Card className="expenses">
            <ExpensesFilter selected={dropDSelection} onDropDownChange={dropDownChangeHandler} />
            {filteredExpenses.map((expense) =>
                <ExpenseItem
                    title={expense.title}
                    amount={expense.amount}
                    date={expense.date}>
                </ExpenseItem>)};
        </Card>

    );
}

export default Expenses;
